$(function($) {

    $(window).load(function() {

        "use strict";

        $("#loader").delay(400).fadeOut();
        $(".animationload").delay(400).fadeOut("fast");

    });

    $(window).scroll(function() {

        "use strict";

        var b = $(window).scrollTop();

        if (b > 84) {
            $("header").addClass("nav-fix");
        } else {
            $("header").removeClass("nav-fix");
        }

    });

    /// Owl-carousel ///
    if ($("#full").length > 0) {
        $("#full").owlCarousel({
            loop: true,
            autoplay: true,
            autoplayTimeout: 25000,
            autoplayHoverPause: true,
            items: 1,
            dots: true,
            animateOut: 'fadeOut',
            autoHeight: true
        });
    };
    if ($("#inner-hero").length > 0) {
        $("#inner-hero").owlCarousel({
            loop: true,
            autoplay: true,
            autoplayTimeout: 25000,
            autoplayHoverPause: true,
            items: 1,
            animateOut: 'fadeOut',
            autoHeight: true,center:true
        });
    };
    if ($("#rank").length > 0) {
        $("#rank").owlCarousel({
            loop: true,
            autoplay: true,
            autoplayTimeout: 25000,
            autoplayHoverPause: true,
            items: 1,
            dots: true,
            animateOut: 'fadeOut',
            URLhashListener: true,
            autoplayHoverPause: true,
            startPosition: 'URLHash'
        });
    };

    if ($("#gift").length > 0) {
        $("#gift").owlCarousel({
            loop: true,
            dots: true,
            lazyFollow: false,
            responsive: {
                0: {
                    items: 2
                },
                768: {
                    items: 3
                },
                992: {
                    items: 4
                },
            },
        });
    };
    if ($("#activity-news").length > 0) {
        $("#activity-news").owlCarousel({
            loop: true,
            dots: true,
            lazyFollow: false,
            responsive: {
                0: {
                    items: 3
                },
                768: {
                    items: 4
                },
                992: {
                    items: 5
                },
            },
        });
    };
    if ($("#activity-sponsor").length > 0) {
        $("#activity-sponsor").owlCarousel({
            loop: true,
            dots: true,
            lazyFollow: false,
            responsive: {
                0: {
                    items: 3
                },
                768: {
                    items: 4
                },
                992: {
                    items: 5
                },
            },
        });
    };
    if ($("#activity-video").length > 0) {
        $("#activity-video").owlCarousel({
            loop: true,
            nav: true,
            lazyFollow: false,
            responsive: {
                0: {
                    items: 3
                },
                768: {
                    items: 4
                },
                992: {
                    items: 5
                },
            },
        });
    };
    /// Wow ///
    var wow = new WOW({
        offset: -500
    });
    wow.init();

    /// Go Top ///
    $(window).scroll(function() {
        if ($(this).scrollTop()) {
            $('.back-to-top button').fadeIn();
        } else {
            $('.back-to-top button').fadeOut();
        }
    });
    $(".back-to-top button").on('click', function() {
        $("html, body").animate({
            scrollTop: 0
        }, 1000);
    });
    /// Facebook Show ///
    $('.fb-show').on('click', function() {
        $('div.fb-content').toggle(600);
    });

});